/**
 * @name formatPublicationDate
 * @description Mise en forme d'une date à partir d'un timestamp, peut retourner il y a X minutes, ou X heures, ou une date précise en fonction de la date actuelle
 * @param {Integer} timestamp
 * @returns {string} Date formatée
 */
function formatPublicationDate(timestamp){
    var a = new Date(timestamp * 1000);
    var n = Date.now();
    var mn_spent = ((n - a)/1000) / 60;
    var time ="";
    if(mn_spent < 60){
        time = 'Il y a ' + Math.floor(mn_spent) + ' min';
    }else if(mn_spent < 1440){
        time = 'Il y a ' + Math.floor(mn_spent / 60) + ' heures';
    }else{
        var months = ['Janvier','Février','Mars','Avril','Mai','Juin','Juillet','Août','Septembre','Octobre','Novembre','Décembre'];
        var year = a.getFullYear();
        var month = months[a.getMonth()];
        var date = a.getDate();
        var hour = a.getHours();
        var min = a.getMinutes();
        var time = 'Le ' + date + ' ' + month + ' ' + year + ' à ' + hour + 'h' + min;
    }
    return time;
}


/**
 * @name populateComment
 * @description Met en forme un template de commentaire à partir d'un objet commentaire
 * @param {Object} comment Objet commentaire à styliser
 * @returns {string} Template de commentaire formaté
 */
function populateComment(comment, c){
    cl('CMNT: populating comment: ' + comment);
    var comment_tpl;
    if(!comment.deleted){ // Si commentaire pas supprimé
        comment_tpl = c.tpl.empty;

        // Gestion des attributs
        var attributs = '';
        attributs += comment.isUserComment ? 'data-owner="true" ' : '';
        attributs += comment.canBeReported ? 'data-reportable="true" ' : '';
        attributs += comment.userLike ? 'data-liked="true" ' : '';
        attributs += comment.parent_id !== 0 ? 'data-parent-id="'+comment.parent_id+'" ' : '';
        attributs += 'data-comment-id="'+comment.comment_id+'" ';
        comment_tpl = comment_tpl.replace('##attributs##=""', attributs);

        // Gestion des classes
        var classes ='';
        classes += comment.parent_id !== 0 ? c.answer_cmnt_class : c.root_cmnt_class;
        comment_tpl = comment_tpl.replace('##classes##', classes);

        // Mise en forme du name et remplacement dans le tpl
        var author = '';
        if(comment.firstname !== undefined && comment.firstname !== ''){
            author += comment.firstname;
        }
        if(comment.lastname !== undefined && comment.lastname !== ''){
            author += ' ' + comment.lastname.slice(0,1) + '.';
        }
        comment_tpl = comment_tpl.replace('##author##', author);


        // Compteur de like
        var likes_count = '0';
        if(comment.likes_count !== undefined && comment.likes_count !== ''){
            likes_count = comment.likes_count;
        }
        comment_tpl = comment_tpl.replace('##likes_count##', likes_count);

        // Image user
        var picture = '';
        var default_picture = comment_tpl.slice(comment_tpl.indexOf('data-default-picture'));
        default_picture = default_picture.replace('data-default-picture="', '');
        default_picture = default_picture.slice(0, default_picture.indexOf('">'));
        if(comment.picture !== undefined && comment.picture !== ''){
            picture = comment.picture;
        }else{
            picture = default_picture;
        }
        comment_tpl = comment_tpl.replace('##picture##', picture);

        // Liens vers la page de l'user
        var user_page = '#';
        if(comment.user_page !== undefined && comment.user_page !== ''){
            user_page = comment.user_page;
        }
        comment_tpl = comment_tpl.replace('##user_page##', user_page);

        // Commentaire
        var comment_content = '';
        var caracter_limit = 120; // 2 lignes max pour "voir la suite"
        if(comment.comment_content !== undefined && comment.comment_content !== ''){
            if(comment.comment_content.length > 120){
                comment_content += comment.comment_content.slice(0, 120);
                comment_content += '<span class="cmnt__more">... lire la suite</span>';
                comment_content += '<span class="cmnt__more-content">'+comment.comment_content.slice(120)+'</span>';
                comment_content += '<div class="cmnt__less">lire moins</div>';
            }else{
                comment_content = comment.comment_content;
            }
        }
        comment_tpl = comment_tpl.replace('##comment_content##', comment_content);

        // Mise en forme de la date de publication + insertion
        var date_publication = '';
        if(comment.date_publication !== undefined && comment.date_publication !== ''){
            date_publication = formatPublicationDate(comment.date_publication);
        }
        comment_tpl = comment_tpl.replace('##date_publication##', date_publication);

    }else{
        comment_tpl = c.tpl.message;
        comment_tpl = comment_tpl.replace('##webmaster_message##', 'Ce contenu a été supprimé').replace('##date_publication##', formatPublicationDate(comment.date_deleted));

        // Gestion des classes
        var classes ='';
        classes += comment.parent_id !== 0 ? c.answer_cmnt_class : c.root_cmnt_class;
        comment_tpl = comment_tpl.replace('##classes##', classes);

        // Gestion des attributs
        var attributs = '';
        attributs += comment.parent_id !== 0 ? 'data-parent-id="'+comment.parent_id+'" ' : '';
        attributs += 'data-comment-id="'+comment.comment_id+'" ';
        comment_tpl = comment_tpl.replace('##attributs##=""', attributs);
    }
    return comment_tpl;
}

/**
 * @name loadAllComments
 * @description Affichage d'une liste de commentaire depuis un object. Fonction chargée au lancement de la page grâce à l'objet comment_datas présent dans comment.tpl
 * @param {Object} comment_datas Object comportant la liste de tous les commentaires à charger
 */
function loadAllComments(comment_datas, c){
    cl('CMNT: Loading all comments from datas: ' + comment_datas);
    c.$root.trigger('cmnt:loading');
    var logged = false;
    if(comment_datas.isUserLogged !== undefined){
        logged = comment_datas.isUserLogged;
    };
    $.each(comment_datas.comments, function(i, comment){
        c.$comment_receiver.append(populateComment(comment, c));
        if(comment.answers !== false){
            $.each(comment.answers, function(i, answer){
                c.$comment_receiver.append(populateComment(answer, c));
            });
        }
    });
    c.$root.trigger('cmnt:loaded');
    displayComments('minimal', '', c);
}

/**
 * @name loadUniqueComment
 * @description Création d'un commentaire unique
 * @param {*} comment Object commentaire
 */
function loadUniqueComment(comment, c){
    cl('CMNT: Ajout d\'un commentaire unique');
    c.$root.trigger('cmnt:loading');
    if(comment.parent_id === 0){
        cl('CMNT: Nouveau commentaire ajout au début de la liste');
        // Nouveau commentaire => ajout au début de la liste
        c.$comment_receiver.prepend(populateComment(comment, c));
    }else{
        // Réponse ==> ajout à la fin de la liste sous le parent
        cl('CMNT: Commentaire est une réponse, ajout à la fin de la liste sous le parent');
        if(c.$each_cmnt_answer.filter('[data-parent-id="'+comment.parent_id+'"]').length){ // S'il y a déjà des réponses
            c.$each_cmnt_answer.filter('[data-parent-id="'+comment.parent_id+'"]').last().after(populateComment(comment, c));
        }else{
            c.$each_cmnt.filter('[data-comment-id="'+comment.parent_id+'"]').after(populateComment(comment, c));
        }
    }
    c.$root.trigger('cmnt:loaded');
}

/**
 * @name displayComments
 * @description Permet de gérer l'affichage des commentaires, d'en afficher plus/moins
 * @param {string} type minimal|comment|answer Minimal: Affiche 3 comment max et 3 answer max.  Comment: Affiche tous les comments. Answer: Affiche toutes les réponses d'un commentaire
 * @param {int} parent_id Optionnel, va de paire avec le type 'answer', représente l'id du commentaire parent
 */
function displayComments(type, parent_id, c){
    c.$root.trigger('cmnt:loading');
    type = type || 'minimal';
    parent_id = parent_id || 0;

    /**
     * @description Affiche les 3 premières réponses d'un commentaire donné, et si + de 3, ajoute un bouton "voir plus"
     * @param {int} parent_id
     */
    function limitAnswers(parent_id){
        c.$each_cmnt_answer.filter('[data-parent-id="'+parent_id+'"]').slice(0,3).show();
        $('.answ-display-more').remove();
        if(c.$each_cmnt_answer.filter('[data-parent-id="'+parent_id+'"]').length > 3){
            c.$each_cmnt_answer.filter('[data-parent-id="'+parent_id+'"]').eq(3).after(c.tpl.more_answers.replace('##parentid##', parent_id));
        }
    }

    if(type == 'minimal'){ // Affiche 3 comment et 3 réponses max
        cl('CMNT: Affichage 3 comments et 3 réponses max');
        c.$each_cmnt.hide();
        c.$each_cmnt_root.add(c.$each_cmnt_message).slice(0,3).each(function(i, el){
            $(this).show();
            var current_parent_id = $(this).attr('data-comment-id');
            limitAnswers(current_parent_id);
        });
        if(c.$each_cmnt_root.length > 3 ){ // Si + de 3 commentaires le bouton affiche + de comment
            cl('CMNT: Plus de 3 commentaires, affichage du bouton + de commentaires');
            c.$display_more_cmnt.off('click').on('click', function(){
                displayComments('comment', '', c);
            })
                .removeClass('less')
                .find('.btn-text').text('Plus de commentaires')
                .show();
        }else{
            c.$display_more_cmnt.hide();
        }


    }else if(type == 'comment'){ // Affiche tous les comments 3 réponses max
        cl('CMNT: Affiche tous les comments, 3 réponses max');
        c.$each_cmnt_root.each(function(i, el){
            $(this).show();
            var current_parent_id = $(this).attr('data-comment-id');
            limitAnswers(current_parent_id);
        });
        c.$display_more_cmnt.off('click').on('click', function(){ // le bouton affiche - de comment
            displayComments('minimal', '',  c);
        })
        .addClass('less')
        .find('.btn-text').text('Moins de commentaires');


    }else if(type == 'answer'){ // Affiche toutes les réponse d'un commentaire précis
        cl('CMNT: Affiche toutes les réponses du commentaire ' + parent_id);
        if(parent_id !== 0){
            c.$each_cmnt_answer.filter('[data-parent-id="'+parent_id+'"]').show();
        }else{
            cl('CMNT: Error, missing "parent_id" argument', 'error');
        }
    }else{
        cl('CMNT: Error, wrong value for "type" argument', 'error');
    }
    c.$root.trigger('cmnt:loaded');
}

/**
 * @name majCommentCount
 * @description Met à jour le compteur de commentaires
 */
function majCommentCount(c){
    var count = c.$each_cmnt_root.filter(':not(.message)').length;
    c.$cmnt_count.html(count);
}


