function manageNavigationDisplay(e, $prev, $next) { // Ne fonctionne pas avec loop: true
	var current_index = getRealCurrentIndex(e);
	if (current_index == 1) {
		$prev.addClass('disabled');
	} else {
		$prev.removeClass('disabled');
	}
	if ((current_index + (e.page.size - 1 )) == e.item.count) {
		$next.addClass('disabled');
	} else {
		$next.removeClass('disabled');
	}
}

/**
 * @description Fixe les problèmes de wrong index dans plusieurs conditions, notamment avec loop = true
 * @param {*} e event Owl
 */
function getRealCurrentIndex(e){
	total = e.item.count;
	current = (e.item.index + 1) - e.relatedTarget._clones.length / 2;
	if (current > total || current == 0) {
        current = total - (current % total);
    }
	return current;
}

/**
 * @name updatePager
 * @param {event} e - event from owl carousel
 * @param {Object} s - Master slider object
 */
function updatePager(e, s) {
	var current, total, prev, next;
	total = e.item.count;
	current = getRealCurrentIndex(e);
	if (current == 1) {
		prev = total;
	} else {
		prev = current - 1;
	}
	if (current == total) {
		next = 1;
	} else {
		next = current + 1;
	}
	s.main.pager.$current.html(current);
	s.main.pager.$total.html(total);
	s.main.pager.$prev.html(prev);
	s.main.pager.$next.html(next);
}


function buildDots($li, $dots, $dot_template){
	$li.each(function(index, element){
		var $dot = $dot_template.clone();
		index ++;
		$dot.html(index).attr('data-slider-index', (index - 1));
		$dots.append($dot);
	});
}

function majActiveDot(e, s){
	var real_current = getRealCurrentIndex(e) - 1;
	$current_dot = s.main.dot.$dot.filter('[data-slider-index="'+real_current+'"]');
	s.main.dot.$dot.removeClass('active');
	$current_dot.addClass('active');
}


/**
 * @description Maj les infos textes dynamique par dessus un slider
 * @param {*}  s 
 */
function updateSliderText(e, s) {
	var $currentSlide = s.main.$slider.find('.owl-item:eq(' + e.item.index + ') li > div');
	var infos_to_change = [];
	if(s.main.text.infos.length){
		s.main.text.infos.forEach(function(info, index){
			if($currentSlide.attr(info.data) !== undefined && $currentSlide.attr(info.data) != '' && $currentSlide.attr(info.data) != ' '){
				infos_to_change.push(index);
			}
		});
		if(infos_to_change.length > 0){
			s.main.text.infos.forEach(function(info, index){
				var $receiver = info.$selector;
				var data = $currentSlide.attr(info.data);
				if(info.type == "text"){
					if (typeof data == 'undefined'){
						data = '';
						$receiver.hide();
					}else{
						$receiver.show();
					}
					$receiver.html(data);
				}else if(info.type == "href"){
					$receiver.attr('href', data);
				}else if(info.type == "background"){
					$receiver.css('background-image',data);
				}
			});
			s.main.text.$root.show();
		}else{
			s.main.text.$root.hide();
		}	
	}
}

/**
 * @description Attache les écouteurs sur les arrows
 * @param {*} s 
 */
function attachCustomArrowsEvents(s, $slider, $prev, $next) {
	cl('Slider Addons: attaching custom nav events');
	$prev.add($next).off('click');
	$prev.on('click', function () {
		if(!s.can_animate) return;
		if(s.autoplay !== undefined && s.autoplay.has_autoplay)destroyAutoPlayInterval(s);
		//s.can_animate = false;
		$slider.trigger('prev.owl.carousel');
	});
	$next.on('click', function () {
		if(!s.can_animate) return;
		if(s.autoplay !== undefined && s.autoplay.has_autoplay)destroyAutoPlayInterval(s);
		//s.can_animate = false;
		$slider.trigger('next.owl.carousel');
	});
}

/**
 * @description start l'autoplay du slider
 * @param {*} s 
 */
function buildAutoPlayInterval(s){
	s.autoplay.$playpause.removeClass('play').addClass('pause');
	s.autoplay.interval = setInterval(function(){
		s.$slider.trigger('next.owl.carousel');
	}, s.autoplay.timeout);
}

/**
 * @description Stop l'autoplay du slider
 * @param {*} s 
 */
function destroyAutoPlayInterval(s){
	clearInterval(s.autoplay.interval);
	s.autoplay.$playpause.removeClass('pause').addClass('play');
}

